import React, { useState } from 'react';
import Menu from './Menu';

const Burger = () => {
    const [isOpen, setIsOpen] = useState(false);
    return (
        <>
        <div onClick={() => setIsOpen(!isOpen)} className="burger" aria-label="Toggle menu">
            <span />
            <span />
            <span />
        </div>
           {isOpen && <Menu />}
           </>
    )
}

export default Burger;
