import React, { Component, useState } from 'react';
import curry from './Images/curry.jpg';
import eagles from './Images/eagles.jpg';
import odellAndSaquon from './Images/odell-and-saquon.jpg';
import saquon from './Images/saquon.jpg';
import fatherDaughter from './Images/father-daughter.jpg';
import robinWilliams from './Images/robin.jpg';
import walterWhite from './Images/walter-white.jpg';
import tevlin from './Images/tevlin.jpg';
import titanic from './Images/titanic.jpg';
import titanicFrame from './Images/titanic-frame.jpg';

interface IGalleryState {
  images: [];
}
const Gallery: React.FC = () => {
  const [images, setImages] = useState(photos);

  const renderDrawings = () => {
    const drawings = images.map((img) => img.id.includes('drawing'));
    useState({ images: drawings });
  };
  const renderPaintings = () => {
    const paintings = images.map((img) => img.id.includes('painting'));
    useState({ images: paintings });
  };
  const renderPrints = () => {
    const prints = images.map((img) => img.id.includes('print'));
    useState({ images: prints });
  };
  const renderMisc = () => {
    const misc = images.map((img) => img.id.includes('misc'));
    useState({ images: misc });
  };

  return (
    <div className="gallery-grid">
      <div className="gallery-type">
        <span onClick={renderDrawings}>Drawings</span>
        <span onClick={renderPaintings}>Paintings</span>
        <span onClick={renderPrints}>Prints</span>
        <span onClick={renderMisc}>Misc</span>
      </div>
      {photos.map((photo, index) => (
        <img
          src={photo.src}
          alt={photo.alt}
          key={index}
          height={photo.height}
          width={photo.width}
          id={photo.id}
        />
      ))}
    </div>
  );
};

const photos = [
  {
    src: curry,
    width: 'auto',
    height: 400,
    alt: '',
    id: 'drawing',
  },
  {
    src: eagles,
    width: 'auto',
    height: 300,
    id: 'drawing',
  },
  {
    src: odellAndSaquon,
    width: 'auto',
    height: 500,
    id: 'drawing',
  },
  {
    src: saquon,
    width: 'auto',
    height: 300,
    id: 'drawing',
  },
  {
    src: fatherDaughter,
    width: 300,
    height: 'auto',
    id: 'drawing',
  },
  {
    src: robinWilliams,
    width: 400,
    height: 'auto',
    id: 'drawing',
  },
  {
    src: walterWhite,
    width: 470,
    height: 'auto',
    id: 'drawing',
  },
  {
    src: tevlin,
    width: 300,
    height: 'auto',
    id: 'drawing',
  },
  {
    src: titanic,
    width: 500,
    height: 'auto',
    id: 'painting',
  },
  {
    src: titanicFrame,
    width: 500,
    id: 'painting',
  },
];

export default Gallery;
